public class Main {
    public static void main(String[] args) {
        Solution s=new Solution();
        String str="";
//        System.out.println(s.minimumTime(str));
        printRatio(str);
//      print();
    }
    static void print(){
        System.out.println(Integer.toBinaryString(12345679));
    }
    static void printRatio(String s){
        int i;
        int cnt=0;
        double ratio=0;
        for (i = 0; i < s.length(); i++) {
            cnt+=s.charAt(0)-'0';
            ratio=1.0*cnt/(i+1);
            if(ratio!=0&&ratio<=0.5) break;
        }
        System.out.println(i);
    }
}
